mapper.select:
/** MainMapper.selectList **/ select user_srno , list_srno , list_subject , list_content , reg_id , cast(reg_dt AS Date) from sj_community.tb_list where 1=1
and list_subject like '%'||#{keyword}||'%'
and valid_yn = 'Y'

mapper.insert:
    	/** MainMapper.insertList **/

			select 
				coalesce(max(list_srno),0) + 1 
			from sj_community.tb_list a
			where a.user_srno = #{userSrno}


    	insert into sj_community.tb_list (
    		user_srno
    		,list_srno
    		,list_subject
    		,list_content
    		,reg_id
    		,reg_dt
    		,valid_yn
    	)
    	values (
    		#{userSrno}
    		,#{listSrno}
    		,#{listSubject}
    		,#{listContent}
    		,#{regId}
    		,to_char(now()::date, 'yyyymmdd')
    		,'Y'
    	)

mapper.update:
/** MainMapper.updateList **/
update 
	sj_community.tb_list
set 
	list_subject = #{listSubject}
	,list_content = #{listContent}
	,reg_dt = to_char(now()::date, 'yyyymmdd')
where 1=1
and user_srno = CAST(#{userSrno} AS NUMERIC)
and list_srno = CAST(#{listSrno} AS NUMERIC)
